import React, { useState } from 'react';
import styles from './currencyCalculator.module.css';

export default function CurrencyCalculator(props) {
  const [inputValue, setInputValue] = useState(0);
  const [outputValue, setOutputValue] = useState(0);
  console.log(props.selected);

  const calculateInput = (output) => {
    const result = parseFloat(output) * parseFloat(props.selected.deal_bas_r || 1);
    setInputValue(result.toFixed(2));
  }

  const calculateOutput = (input) => {
    const result = parseFloat(input) / parseFloat(props.selected.deal_bas_r || 1);
    setOutputValue(result.toFixed(2));
  }

  return (
    <div className={styles.calculator_container}>
      <p className={styles.calculator_title}>환율 계산기</p>
      {
        props.selected ?
          <div className={styles.calculator}>
            <div className={styles.calculator_input_container}>
              <label htmlFor="currency_input">{`한국 원(KRW)`}</label>
              <input className={styles.calculator_input} type="text" value={inputValue} name="currency_input" onChange={(e) => calculateOutput(e.target.value)} />
            </div>
            <div className={styles.calculator_output_container}>
              <label htmlFor="currency_output">{`${props.selected.cur_nm}(${props.selected.cur_unit})`}</label>
              <input className={styles.calculator_output} type="text" value={outputValue} name="currency_output" onChange={(e) => calculateInput(e.target.value)} />
            </div>
          </div>
        :
        <h3>비교할 환율을 선택해주세요</h3>
      }
    </div>
  );
}
