import React, { useRef, useState, useEffect } from 'react';
import styles from './currencyList.module.css';
import Panel from '../components/panel';
import CurrencyBox from '../components/currencyBox';

export default function CurrencyList() {
  const [currencyData, setCurrencyData] = useState([]);
  const [selectedCurrency, setSelectedCurrency] = useState(null);
  const inputRef = useRef(0);
  const outputRef = useRef(0);

  const calculateInput = () => {
    if (outputRef.current.value.trim() === "") {
      inputRef.current.value = 0;
      return;
    }

    if (outputRef.current.value >= 0) inputRef.current.value = (parseFloat(outputRef.current.value) * parseFloat(selectedCurrency.deal_bas_r.replace(/,/g, ""))).toFixed(2);
    else inputRef.current.value = 0;
  }

  const calculateOutput = () => {
    if (inputRef.current.value.trim() === "") {
      outputRef.current.value = 0;
      return;
    }

    if (inputRef.current.value >= 0) outputRef.current.value = (parseFloat(inputRef.current.value) / parseFloat(selectedCurrency.deal_bas_r.replace(/,/g, ""))).toFixed(2);
    else outputRef.current.value = 0;
  }

  const resetValue = () => {
    inputRef.current.value = 0;
    outputRef.current.value = 0;
  }

  const getData = async () => {
    const mock_API = 'https://67283275270bd0b97554a345.mockapi.io/currency';
    const currency_API = '/exchangeJSON?authkey=3wNf5tfXGjytedr8fF3AUEljbd30YBED&data=AP01';

    try {
      const response = await fetch(currency_API);
      const data = await response.json();
      setCurrencyData(data);
      setSelectedCurrency(data[0]);
    } catch (error) {
      console.error('Error fetching exchange rate:', error);
      throw error;
    }
  }

  const setTarget = (data) => {
    resetValue();
    setSelectedCurrency(data);
  }

  useEffect(() => {
    getData();
  }, []);

  return (
    <div className={styles.page_container}>
      <Panel selected={selectedCurrency} getData={getData} />
      <CurrencyBox data={currencyData} setTarget={setTarget} />
    </div>
  );
}
