import React, { useRef, useState } from 'react';
import styles from './currencyCalculator.module.css';

export default function CurrencyCalculator(props) {
  const [baseValue, setBaseValue] = useState(0);
  const [targetValue, setTargetValue] = useState(0);

  const calculateInput = (value) => {
    if (value.trim() === "") {
      setBaseValue(0);
      return;
    }

    if (value >= 0) setBaseValue((parseFloat(targetValue) * parseFloat(props.selected.deal_bas_r.replace(/,/g, ""))).toFixed(2));
    else setBaseValue(0);
  }

  const calculateOutput = (value) => {
    if (value.trim() === "") {
      setTargetValue(0);
      return;
    }

    if (value >= 0) setTargetValue((parseFloat(baseValue) / parseFloat(props.selected.deal_bas_r.replace(/,/g, ""))).toFixed(2));
    else setTargetValue(0);
  }

  const resetCalculator = () => {
    setBaseValue(0);
    setTargetValue(0);
  }

  return (
    <div className={styles.calculator_container}>
      <p className={styles.calculator_title}>환율 계산기</p>
      {
        props.selected ?
          <div className={styles.calculator}>
            <div className={styles.calculator_input_container}>
              <label htmlFor="currency_input">{`한국 원(KRW)`}</label>
              <input className={styles.calculator_input} type="text" value={baseValue} name="currency_input" onChange={(e) => calculateOutput(e.target.value)} />
            </div>
            <div className={styles.calculator_output_container}>
              <label htmlFor="currency_output" onChange={resetCalculator}>{`${props.selected.cur_nm}(${props.selected.cur_unit})`}</label>
              <input className={styles.calculator_output} type="text" value={targetValue} name="currency_output" onChange={calculateInput} />
            </div>
          </div>
        :
        <h3>비교할 환율을 선택해주세요</h3>
      }
    </div>
  );
}
