import React, { useState, useEffect } from 'react';
import styles from './currencyList.module.css';
import Panel from '../components/panel';
import CurrencyBox from '../components/currencyBox';

export default function CurrencyList() {
  const [currencyData, setCurrencyData] = useState([]);
  const [selectedCurrency, setSelectedCurrency] = useState(null);

  const getData = async () => {
    const url = `/exchangeJSON?authkey=3wNf5tfXGjytedr8fF3AUEljbd30YBED&data=AP01`;

    try {
      const response = await fetch(url);
      const data = await response.json();
      setCurrencyData(data);
      console.log(data);
      setSelectedCurrency(data[0]);
    } catch (error) {
      console.error('Error fetching exchange rate:', error);
      throw error;
    }
  }

  const getFormattedDate = (date) => {
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, '0'); // 월은 0부터 시작하므로 +1
    const day = String(date.getDate()).padStart(2, '0');
    return `${year}${month}${day}`;
  };

  const fetchDatas = async () => {
    const today = new Date();

    for (let i=0; i<30; i++) {
      const pastDate = new Date(today);
      pastDate.setDate(today.getDate() - i);
      const url = `/exchangeJSON?authkey=3wNf5tfXGjytedr8fF3AUEljbd30YBED&searchdate=${getFormattedDate(pastDate)}&data=AP01`;
      const response = await fetch(url);
      const data = await response.json();
      console.log(data);
    }
  };

  useEffect(() => {
  });

  return (
    <div className={styles.page_container}>
      <Panel selected={selectedCurrency} getData={getData} fetchData={fetchDatas}/>
      <CurrencyBox data={currencyData}/>
    </div>
  );
}
